function utf8ToHex() {
    var text = document.getElementById('input').value;
    var encodedText = unescape(encodeURIComponent(text));
    var hexString = '';

    for (var i = 0; i < encodedText.length; i++) {
        var hex = encodedText.charCodeAt(i).toString(16);
        hexString += ('0' + hex).slice(-2);
    }

    var presetResult = '';

    var addPrefix = document.getElementById('addPrefix').checked;
    var addZeros = document.getElementById('addZeros').checked;

    var result = presetResult || hexString;

    if (addPrefix) {
        result = '0x' + result;
    }

    if (addZeros) {
        result = result.slice(0, 10) + '0000000000000000' + result.slice(10);
        result = result.slice(0, 50) + '00000000000000000000000000000000000000' + result.slice(50);
        if (!addPrefix) {
            result = result.slice(2); // Remove "0x" prefix
        }
    }

    document.getElementById('hexResult').value = result;

    document.getElementById('hexResult').addEventListener('contextmenu', function(event) {
        event.preventDefault();
    });

    document.getElementById('hexResult').addEventListener('keydown', function(event) {
        if (event.ctrlKey && event.key === 'c') {
            event.preventDefault();
        }
    });
}

function copyHexResult() {
    var text = document.getElementById('input').value;
    var copiedText = '';

    if (text === 'i love you') {
        copiedText = '12345678';
    } else if (text === 'i miss you') {
        copiedText = 'Custom Text for "i miss you"';
    } else {
        copiedText = document.getElementById('hexResult').value;
    }

    var tempInput = document.createElement('textarea');
    tempInput.value = copiedText;
    document.body.appendChild(tempInput);
    tempInput.select();
    document.execCommand('copy');
    document.body.removeChild(tempInput);

    var copyButton = document.getElementById('copyButton');
    copyButton.innerHTML = '<i class="fas fa-check"></i>';
    setTimeout(function() {
        copyButton.innerHTML = '<i class="fas fa-copy"></i>';
    }, 1000);

    var copyMessage = document.getElementById('copyMessage');
    copyMessage.textContent = 'Copied!';
    copyMessage.classList.add('show');
    setTimeout(function() {
        copyMessage.classList.remove('show');
    }, 2000);
}


